import React, { Fragment, useCallback, useRef, useState } from 'react'
import rose from '../images/rose.jpeg'
import daisy from '../images/daisy.webp'
import lavender from '../images/lavender.jpeg'
import lilly from '../images/lilly.jpeg'
import sunflower from '../images/sunflower.jpeg'
import tulip from '../images/tulip.jpeg'

export default function Home() {

const imageCollection = [
        {
            id : 0,
            url : rose,
            title : 'rose',
        },
        {
            id : 1,
            url : daisy,
            title : 'daisy',
        },
        {
            id : 2,
            url : lavender,
            title : 'lavender',
        },
        {
            id : 3,
            url : lilly,
            title : 'lilly',
        },
        {
            id : 4,
            url : sunflower,
            title : 'sunflower',
        },
        {
            id : 5,
            url : tulip,
            title : 'tulip',
        },     
        {
            id : 6,
            url : rose,
            title : 'rose',
        },
        {
            id : 7,
            url : daisy,
            title : 'daisy',
        },
        {
            id : 8,
            url : lavender,
            title : 'lavender',
        },
        {
            id : 9,
            url : lilly,
            title : 'lilly',
        },
        {
            id : 10,
            url : sunflower,
            title : 'sunflower',
        },
        {
            id : 11,
            url : tulip,
            title : 'tulip',
        },     
];


const [count, setCount] = useState(0);
const [clickedIndex, setClickedIndex] = useState();
const [clickedTitle,setClickedTitle] = useState();
// const [matchFound,setMatchFound] = useState([]);
let [score, setScore] = useState(0);
const matchFound = [];
const countRef = useRef(0);

let prevClickedTitle = '';
let currentTitile = '';


const handleCount = () =>{
    countRef.current += 1;
    setCount(countRef.current)
}

const handleClick = useCallback((item) => {
    setClickedIndex(item.id);
    currentTitile = item.title;
    prevClickedTitle = clickedTitle;

    console.log('prevClickedTitle',prevClickedTitle);
    console.log('currentTitile',currentTitile);
    setClickedTitle(currentTitile)

    if(prevClickedTitle === currentTitile){
        score +=1;
        matchFound.push(currentTitile);
        console.log(matchFound);
        setScore(score);
    }
});


  return (
    <Fragment>
            <div className='mt-5 mb-5 flex justify-end mr-[50px]'>Click Counter : {count}</div>
            <div className='mt-5 mb-5 flex justify-end mr-[50px]'>score : {score}</div>
            <div className='black grid imageGrid m-[50px] gap-5' onClick={handleCount}>
            {
                        imageCollection.map((item,index) =>{
                            return(
                                <div 
                                className={`flex justify-center border rounded-sm p-5 cursor-pointer ${index === clickedIndex || matchFound.find(title => title === item.title) ? 'bg-white' : 'bg-black'} h-[200px]`} 
                                key={index} 
                                onClick={() => handleClick(item)}>     
                                    <img 
                                        className={`md:h-[150px] ${index === clickedIndex || matchFound.find(title => title === item.title) ? 'block' : 'hidden'}`} 
                                        src={item.url} 
                                        alt={item.title}>
                                    </img>               
                                </div>
                            )
                         })
            }
        </div>
    </Fragment>
  )
}
