import React, { Fragment, useEffect, useRef, useState } from 'react'
import rose from '../images/rose.jpeg'
import daisy from '../images/daisy.webp'
import lavender from '../images/lavender.jpeg'
import lilly from '../images/lilly.jpeg'
import sunflower from '../images/sunflower.jpeg'
import tulip from '../images/tulip.jpeg'

export default function Home() {

const imageCollection = [
        {
            id : 1,
            url : rose,
            title : 'rose',
        },
        {
            id : 2,
            url : daisy,
            title : 'daisy',
        },
        {
            id : 3,
            url : lavender,
            title : 'lavender',
        },
        {
            id : 4,
            url : lilly,
            title : 'lilly',
        },
        {
            id : 5,
            url : sunflower,
            title : 'sunflower',
        },
        {
            id : 6,
            url : tulip,
            title : 'tulip',
        },     
        {
            id : 7,
            url : rose,
            title : 'rose',
        },
        {
            id : 8,
            url : daisy,
            title : 'daisy',
        },
        {
            id : 9,
            url : lavender,
            title : 'lavender',
        },
        {
            id : 10,
            url : lilly,
            title : 'lilly',
        },
        {
            id : 11,
            url : sunflower,
            title : 'sunflower',
        },
        {
            id : 12,
            url : tulip,
            title : 'tulip',
        },     
]
const countRef = useRef(0);

const [count, setCount] = useState(0);
const [clickedIndex, setClickedIndex] = useState();
const [clickedTitle, setClickedTitle] = useState();
const [prevClickedTitle, setPrevClickedTitle] = useState();
let [score, setScore] = useState(0);

const handleClick = () =>{
    countRef.current += 1;
    setCount(countRef.current)
}

  const handleChange = (item) => {
    setClickedIndex(item.id);
    setClickedTitle(item.title);
    setPrevClickedTitle(clickedTitle);
    checkScore();
  };

const checkScore = () =>{
    console.log('prevClickedTitle',prevClickedTitle);
    console.log('clickedTitle',clickedTitle);
    if(prevClickedTitle === clickedTitle){
        score +=1;
        setScore(score);
    }
}

  return (
    <Fragment>
            <div className='mt-5 mb-5 flex justify-end mr-[50px]'>Click Counter : {count}</div>
            <div className='mt-5 mb-5 flex justify-end mr-[50px]'>score : {score}</div>
            <div className='black grid imageGrid m-[50px] gap-5' onClick={handleClick}>
            {
                imageCollection.map((item,index) =>{
                   return(
                    <div className={`flex justify-center border rounded-sm  p-5 cursor-pointer ${index === clickedIndex ? 'bg-white' : 'bg-black'} h-[200px]`} 
                    key={index} 
                    onClick={() => handleChange(item)}>     
                        <img className={`md:h-[150px] ${index === clickedIndex ? 'block' : 'hidden'}`} src={item.url}></img>               
                    </div>
                   )
                })
            }
        </div>
    </Fragment>

  )
}
